mapper.select:
SELECT oi.order_id, oi.order_num, oi.order_time, oi.attractions_id, oi.order_date, oi.user_id, oi.order_total, oi.order_status, ai.attractions_name, su.user_name, su.user_avatar FROM order_info oi LEFT JOIN attractions_info ai ON oi.attractions_id = ai.attractions_id LEFT JOIN sys_user su ON oi.user_id = su.user_id WHERE oi.delete_flag = 0 AND oi.user_id = #{qry.userId}
AND oi.order_num LIKE CONCAT('%', #{qry.orderNum}, '%')
AND oi.order_status = #{qry.orderStatus}
ORDER BY oi.order_status ASC, oi.order_id DESC

SELECT oi.order_id, oi.order_num, oi.order_time, oi.attractions_id, oi.order_date, oi.user_id, oi.order_total, oi.order_status, ai.attractions_name, su.user_name, su.user_avatar FROM order_info oi LEFT JOIN attractions_info ai ON oi.attractions_id = ai.attractions_id LEFT JOIN sys_user su ON oi.user_id = su.user_id WHERE oi.delete_flag = 0
AND oi.user_id = #{qry.userId}
AND oi.attractions_id = #{qry.attractionsId}
AND oi.order_num LIKE CONCAT('%', #{qry.orderNum}, '%')
AND oi.order_status = #{qry.orderStatus}
ORDER BY oi.order_status ASC, oi.order_id DESC
